class Student(object):    def __init__(self, name, score):        self.name = name        self.score = score    def print_score(self):        print('%s score:(%s)'%(self.name, self.score))#       定义一个对象chiudk = Student('chiudk', 98)Leslie = Student('LeslieCheung', 100)#           调用对象内的方法chiudk.print_score()Leslie.print_score()#       添加对象的属性chiudk.age = 18print(chiudk.age)